float Create_Circle(vec2 uv, float radius, vec2 position){
    float d = length(uv-position);
    d = smoothstep(radius, radius - 0.01, d);
   return d;
}
float Create_Smiley(vec2 uv, float radius, vec2 position, float size){

    uv -= position;
    uv *= size;

    float face = Create_Circle(uv, radius, vec2(.0,.0) - Create_Circle(uv, 0.15, vec2(.0,.0) + vec2(.3,.3)) - Create_Circle(uv, 0.15,  vec2(.0,.0) + vec2(-.3,.3)));
    float mouth = Create_Circle(uv,0.5,vec2(0.0,0.0)) - Create_Circle(uv,0.5,vec2(0.0,0.0)+vec2(0, 0.2));
    face -= max(0., mouth);

    return face;
}
vec3 palette(in float t)
{
    vec3 a = vec3(0.500,0.500,0.500);
    vec3 b = vec3(0.500,0.500,0.500);
    vec3 c = vec3(0.998,1.000,1.000);
    vec3 d = vec3(0.000,0.333,0.667);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 2.0;
    float d = length(uv);

    float r = .7;
    vec2 p = vec2(0.,0.);
    vec3 color = palette(1. + iTime);

    //float face = Create_Circle(uv, r, p) - Create_Circle(uv, 0.15, vec2(.3,.3)) - Create_Circle(uv, 0.15, vec2(-.3,.3));
    //float mouth = Create_Circle(uv,0.5,p) - Create_Circle(uv,0.5,p+vec2(0, 0.2));
    //face -= max(0., mouth);

    float face = Create_Smiley(uv, r, vec2(sin(d - iTime),0.0), 2.);
    face += Create_Smiley(uv, r, vec2(sin(d + iTime),0.0), 0.5);

    color = color * face;

    fragColor = vec4(color,1.0);
}
