void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    //sin wave parameters
    float amp = 0.1;
    float period = 50.;
    float timePer = 20.;

    float horizShift = 0.;
    float vertShift = 0.5;

    //line thickness
    float thinckness = 0.05;

    vec3 col;

    //sin wave equation
    float sinequ = amp*sin(period*uv.x-timePer*iTime+horizShift)+vertShift;

    //check if pixel it in range of wave
    if(uv.y < sinequ + thinckness/2. && uv.y > sinequ - thinckness/2.){
        col = vec3(cos(iTime+uv.xyx+vec3(0,2,4)));
    }
    //if not color black
    else{
        col = vec3(0.,0.,0.);
    }

    fragColor = vec4(col,1.0); 

}
