vec2 voronoi_noise_randomVector(vec2 UV, vec2 offset)
{
	mat2 m = 	mat2(15.27, 47.63, 99.41, 89.98);
	UV = fract(sin(UV* m) * 46839.32);
	return vec2(sin(UV.y * +offset.x) * 0.5 + 0.5, cos(UV.x * offset.y) * 0.5 + 0.5);
}
void Voronoi(vec2 UV, vec2 AngleOffset, vec2 CellDensity, out float Out, out float Cells, out float Lines,out float Points)
{
	vec2 g = floor(UV * CellDensity);
	vec2 f = fract(UV * CellDensity);

	float res = 8.0;
	float md=8.0;
    vec2 mr;
	for (int y = -1; y <= 1; y++)
 	{
	    for (int x = -1; x <= 1; x++)
		{
		    vec2 lattice = vec2(x, y);
            vec2 offset = voronoi_noise_randomVector(lattice + g, AngleOffset);
            vec2 r = lattice +offset -f;
		    float d = dot(r,r);

			if (d < res)
			{
			    res = d;
                mr=r;
			}
		}
	}
    res = 8.0;
	for (int y = -1; y <= 1; y++)
 	{
	    for (int x = -1; x <= 1; x++)
		{
		    vec2 lattice = vec2(x, y);
            vec2 offset = voronoi_noise_randomVector(lattice + g, AngleOffset);
            vec2 r = lattice +offset -f;
		    float d = dot(r,r);

			if (d < res)
			{
			    res = d;
			    Out = res;
			    Cells = offset.x;
			}
			if( dot(mr-r,mr-r)>0.00001)
			{
                md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
			}
		}
	}

    Lines = md;
    Points=res;
    //Lines = mix(1.0, 0.0, smoothstep( 0.0, 0.1, md ));
	//Points =smoothstep( 0.1, 0.0, res );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 m = (iMouse.xy*2.0 -iResolution.xy)/ min(iResolution.x, iResolution.y);
    vec3 col= vec3(0.0);
    float a,b,c,d;
    float AA=3.0/min(iResolution.x, iResolution.y);
    Voronoi(p,vec2(4.0),vec2(5.0),a,b,c,d);

    //col =mix( vec3(1.0,0.6,0.0), col, smoothstep( 0.04, 0.07, c) );
    col =mix(col,vec3(1.0,0.0,0.0), smoothstep(AA*5.0,0.0, c ));
    col =mix(col,vec3(1.0,0.0,0.1), smoothstep( AA,0.0, d ));
    if( iMouse.z>0.001 )
    {
        col = mix(col, vec3(1.0,0.0,0.0), smoothstep(AA*5.0, 0.0, abs(length(p-m)-abs(d))));
        col = mix(vec3(0.0), vec3(1.0,0.0,0.0), smoothstep(AA*5.0, 0.0, abs(length(p-m)-abs(d))));
    }

	fragColor = vec4(col,1.0);
}
