void mainImage_spinners( out vec4 O, vec2 U )
{
    float r = 20.,
          p =  6.,
          c = r + p,
          t = 2.*iTime;
    vec2 I = floor(U/c/2.);
    if ( I == floor( vec2(13,7)*fract(1e4*sin(vec2(71,-97)*floor(t)))))
        U += 6.*cos(6.283*2.*t+vec2(0,11)) * (.5-.5*cos(6.283*t));
    O = vec4( 1. - ( length( mod(U , c+c ) - c ) - r ) /2. ); 
}

void mainImage_flashers( out vec4 O, vec2 U )
{
    float r = 20.,
          p =  6.,
          c = r + p,
          t = 3.*iTime;
    vec2 I = floor(U/c/2.);
    if ( I == floor( vec2(13,7)*fract(1e4*sin(vec2(71,-97)*floor(t)))))
        r += 3.-3.*cos(6.283*t);
    O = vec4( 1. - ( length( mod(U , c+c ) - c ) - r ) /2. ); 
}

void mainImage_plain( out vec4 RGBA, in vec2 XY )
{
    float radius  = 20.0;
    float padding =  5.0;
    float cell    = (radius + padding) * 2.0;
    float eps     = 2.0;

    vec2  xy   = mod(XY - iResolution.xy / 2.0 + cell / 2.0, vec2(cell)) - cell / 2.0;
    float dist = length(xy) - radius;
    float c    = smoothstep(eps, 0.0, dist);

    RGBA       = vec4(c);

}


void mainImage( out vec4 RGBA, in vec2 XY )
{
    switch ((int(iTime) / 10) % 3) {
        default:
            mainImage_plain(RGBA, XY);
            break;
        case 1:
            mainImage_flashers(RGBA, XY);
            break;
        case 2:
            mainImage_spinners(RGBA, XY);
            break;
    }
}
