#define PI 3.14159265358979323846264

mat3 xRotation(float angle){
	mat3 rotOut = mat3(1);

    rotOut[1] = vec3(0, cos(angle), sin(angle));
    rotOut[2] = vec3(0, -sin(angle), cos(angle));

    return rotOut;
}

mat3 yRotation(float angle){
	mat3 rotOut = mat3(1);

    rotOut[0] = vec3(cos(angle), 0, sin(angle));
    rotOut[2] = vec3(-sin(angle), 0, cos(angle));

    return rotOut;
}

mat3 zRotation(float angle){
	mat3 rotOut = mat3(1);

    rotOut[0] = vec3(cos(angle), -sin(angle), 0);
    rotOut[1] = vec3(sin(angle), cos(angle), 0);

    return rotOut;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float yaw = (uv.x*2.0-1.0)*PI;
    float pitch = (uv.y*2.0-1.0)*PI/2.0;

    float len = cos(pitch);

    vec3 point;

    point.x = cos(yaw)*len;
    point.y = sin(yaw)*len;
    point.z = sin(pitch);

    //APPLY CHANGES

    point = yRotation(((1.0-iMouse.y/iResolution.y)*2.0-1.0)*PI/2.0)*zRotation((iMouse.x/iResolution.x*2.0-1.0)*PI)*point;

    //END OF CHANGES

    pitch = asin(point.z);
    len = cos(pitch);

    yaw = atan(point.y, point.x);

    uv.x = (yaw/PI+1.0)/2.0;
    uv.y = (pitch*2.0/PI+1.0)/2.0;

	fragColor = texture(iChannel0, uv);
}
