float circleSDF(vec2 c, float r, vec2 uv)
{
    float d = length(c-uv);
    return d - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);
    vec2 m = (iMouse.xy / iResolution.y) - vec2(0.5 * iResolution.x / iResolution.y, 0.5);
    float d1 = circleSDF(m, 0.1, uv);

    fragColor = vec4(0.4, 0.3*cos(100.0*d1 + 10.0*iTime), 0.1, 1.0);
}
