void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    float ratio= iResolution.x/iResolution.y;
    vec2 sc_1 = vec2(0.5*ratio,0.5);
    vec2 sc_2 = vec2(ratio*iMouse.x/iResolution.x,iMouse.y/iResolution.y);



    float freq = 3.0;
    float wave_length = 0.05;
    float amplitude = 0.5;
    float ya=amplitude*sin(2.0*3.1415*(iTime*freq-distance(sc_2,uv)/wave_length));
    float yb=amplitude*sin(2.0*3.1415*(iTime*freq-distance(sc_1,uv)/wave_length));
    float y =ya+yb;
    // Time varying pixel color
    vec3 col =y*vec3(0,1,1);

    // Output to screen
    fragColor = vec4(col,1.0);
}
