#define SPEED 3.
#define DENSITY 2.
#define COMPRESSOR 5.
#define WIDTH .02

vec3 palette( in float t )
{
    vec3 a = vec3(.79,.79,.79);
    vec3 b = vec3(.63,.63,.63);
    vec3 c = vec3(2.,2.,2.);
    vec3 d = vec3(1.,1.333,1.667);
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.y;

    float d = WIDTH/length(uv-vec2(uv.x, sin(uv.x*3.14*DENSITY-iTime*SPEED)/COMPRESSOR));

    float d2 = WIDTH/length(uv-vec2(uv.x, -sin(uv.x*3.14*DENSITY+iTime*SPEED)/COMPRESSOR));

    fragColor = vec4(.1/palette(d+d2),1.);
}
