float hash( float n )
{
    return fract(sin(n)*43758.5453);
}

float noise( in vec2 x )
{
    vec2 p = floor(x);
    vec2 f = fract(x);

    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*57.0;

    return mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
               mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 q = fragCoord.xy/iResolution.xy;
    vec2 p = -1.0 + 2.0*q;

    float f = noise(3.5*p+iTime*3.5)*2.5+1.0;

    vec3 bg = vec3(0.027*10.0+f, 0.149*10.0+f, 0.224*10.0+f);

    float distance = sqrt((fragCoord.y-iMouse.y)*(fragCoord.y-iMouse.y)+(fragCoord.x-iMouse.x)*(fragCoord.x-iMouse.x))+50.0;

    float radius = 20.0/distance;
    vec3 col = vec3(0.027, 0.149, 0.224);
    vec3 light = bg*radius;

    col = col+(light+0.25)/1.5;

    fragColor = vec4(col,1.0);
}
