const float numIter = 500.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = (2.0 * fragCoord - iResolution.xy) / iResolution.y + vec2(-0.6, 0.0);

    vec2 z =  vec2(0.0,0.0);

    float j;
    for(j = 0.0; j < numIter; j++) {

        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (2.0 * z.x * z.y) + c.y;

        z.x = x;
        z.y = y;

        if (length(z) > 10.0) break;

    }

    float blue = 0.0;
    float green = 0.0;
    float red = 0.0;

    if (20.0 < j && j < numIter - (numIter / 10.0)) {
        red = blue = green = pow(j / numIter * 10.0, 2.0);

        green *= (sin(iTime / 1.3) + 2.0) / 2.0;
        blue *= ((sin(iTime / 1.9) + 2.0) / 2.0);
        red *= (cos(iTime/ 1.6) + 2.0) / 2.0;
    }  

    fragColor = vec4(red, green, blue, 1.0);
}
