// Fork of https://shadertoy.com/view/cldBWj 2023-12-03

// JuliaLike.glsl
// press pause and fullScreen to examine fractal:
// mouse.x = horizontal move,  mouse.y = zoom

// z =(2*z*z*z -z*z + a - 2)/(3*z*z -2*z - 1)
// a =(.522375, -.002875)
//   multi p,q
#define M(p,q) vec2(p.x*q.x-p.y*q.y, p.x*q.y+p.y*q.x)

vec4 F(float x) // colorbox
{ vec4 c=vec4(x, .25-x, x-.25, 0);
  c=fract(clamp(fract(c+.75),.25,1.)+.25);
  c=.5+.5*cos(6.28*c*(2.-floor(c+c)));
  c.x=1.-c.x;
  c.w=0.1;
  return c;
}
vec2 itr(vec2 z)
{ vec2 d,e,n,m,a = vec2(.522375, -.002875);
  e.x++;
  n=M(z,z),
  m=M(n,z),
  d = 3.*n-z-z-e,
  z = M((2.*m-n+a-e-e), (d*vec2(1,-1)/dot(d,d)));
  return z;
}
void mainImage(out vec4 O,vec2 v)
{ vec2 R = iResolution.xy,  
  mp = iMouse.xy / R,        // x=horMove, y=zoom
  z = (v+v-R) / R.x / (2.+9.*mp.y),
  e, o, n, m, d;
  z.x += .342 +(sin(iTime*0.1)+2.4  +9.*mp.x);
  e.x++;
  int p;
  float i ,N=1e3 ,B=88., lz; p++;
  vec2 ee=vec2(.001,0), nn, ss, nm, tt; nn++;
  for(; i++<N && length(o-z)>1e-3;)
  { o=z, lz=length(z), z=itr(z);
    nm=(lz-  vec2(length( itr(z+ee)), length( itr(z+ee.yx))))/lz;
    nn++;
    ss+=normalize(nm);
    tt+=(1.-nm)/nn;
  }
  nm = ss / nn;
  O = F(i/B)*1.8*vec4(-.2,.8,-.8,0)*clamp(((dot(nm,e))),.0,1.) +F(i/B);
  i<N ? O : O*=0.1;
  if(p>0) O*=O;
}
