float circle(float radius,float width,float bright,vec2 pos,float center);
float mul=2.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    float mult=mul+iTime/10.;
    vec2 uv = (mult*fragCoord-iResolution.xy)/iResolution.y; uv +=1.+mult;
    uv=mod(uv,1.);

    float mid=0.5;
    vec3 col=vec3(1.,1.,1.);
    const float cstep=.4;
    for(float i=.0;i<=2.;i+=cstep){
        float cir=circle(abs(sin(iTime/2.+i))*0.5,.30,10.,uv,mid);
        if(cir!=1.){
           col=vec3(abs(sin(i)),abs(cos(i)),abs(sin(i*3.+1.)));  
           col*=cir;
        }

    }

    col=-col+1.;
    // Output to screen
    fragColor = vec4(col,1.0);

}

float circle(float radius,float width,float bright,vec2 pos,float center){
    float outp=1.;
    if(abs(length(pos-center)-radius)<=width){
    outp=abs(length(pos-center)-radius);
    outp*=bright;
    }
    return outp;

}
