void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float innerRadius = 100.0;
    float outerRadius = 130.0;
    vec3 circleColor = vec3(0.9058823529411765, 0.2980392156862745, 0.23529411764705882);
    vec3 sectorColor = vec3(0.20392156862745098, 0.596078431372549, 0.8588235294117647);
    vec2 center = iResolution.xy / 2.0;
    float speed = 100.0;

    vec2 sectors[3];
    sectors[0] = vec2(0.0, 10.0);
    sectors[1] = vec2(90.0, 120.0);
    sectors[2] = vec2(180.0, 250.0);


    float dist = distance(center, fragCoord);
    if (dist >= innerRadius && dist <= outerRadius) {
        vec2 p = fragCoord - center;
        float angle = mod(degrees(atan(p.y, p.x)) + 180.0 + 180.0 + iTime * speed, 360.0);

        for(int n = 0; n < 3; ++n) {
            vec2 sector = sectors[n];

            if (angle >= sector.x && angle <= sector.y) {
                fragColor = vec4(sectorColor, 1.0);
                return;
            }
        }

        fragColor = vec4(circleColor, 1.0);
    } else {
        fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
