void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.y;



    float dist = distance(uv, mouse) - 0.05f;

    float light = min(max(1.0f - 20.0 * dist * dist, 0.0), 1.0) * texelFetch(iChannel0, ivec2(1, 0), 0).x;

    vec3 col = texture(iChannel1, uv).xyz * light;


    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 fragCoordInt = ivec2(int(fragCoord.x), int(fragCoord.y));
    float col = 0.0;
    if (fragCoordInt.x == 1 && fragCoordInt.y == 0)
    {
        col = texelFetch(iChannel0, ivec2(1, 0), 0).x;
        if (iMouse.z > 0.0)
        {
            col += iTimeDelta * 2.0;
        }
        else
        {
            col -= iTimeDelta * 5.0;
        }
        col = clamp(col, 0.0, 1.0);
    }

    fragColor = vec4(col, 0.0, 0.0, 0.0);
}
