void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float speed = 0.25 / (iResolution.x / 1280.0);
    float colorResolution = 256.0;

    int x = int(floor(fragCoord.x));
    int y = int(floor(fragCoord.y));
    int positionFactor = (x - y) & (y + x);
    float speedFactor = iTime * speed;
    float outColor = 
    mod(
        mod(
            float(positionFactor) * (speedFactor), 
            float(iResolution.x)
        ), 
        colorResolution
    ) / colorResolution;
    fragColor = vec4(outColor, outColor, outColor, 1.0);
}
