float linearTosRGB(float v)
{
    if(v <= 0.0031308)
      return v * 12.92;

    v = pow(v, 1.0/2.4);
    v *= 1.055;
    v -= 0.055;
    return v;
}

float sRGBToLinear(float v)
{
    if(v <= 0.04045)
        return v / 12.92;

    v += 0.055;
    v /= 1.055;
    v = pow(v, 2.4);
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float value = uv.x;

    float quantization = round(exp2(uv.y * 4.0));//round(1.0 / uv.y);

    value = sRGBToLinear(value);

    float noise = texture(iChannel0, fragCoord/iChannelResolution[0].xy).r;

    value = round(value * quantization + (noise - 0.5))/quantization;

    value = linearTosRGB(value);

    fragColor = vec4(value);
}
