// Experiment with different gamma encoding methods
// Each column in each color is a different gamma encoding
// From left to right: linear, gamma=2.2, and srgb
// Based on https://tomforsyth1000.github.io/blog.wiki.html#%5B%5BThe%20sRGB%20learning%20curve%5D%5D

vec3 SRGBToLinear(vec3 inputCol){


    for (int i=0; i<3; ++i){

        if( inputCol[i] < 0.04045 )
            inputCol[i] /= 12.92;
        else
            inputCol[i] = pow((inputCol[i] + 0.055)/1.055,2.4);

    }
    return inputCol;

}

vec3 panelColor(in vec2 uv, in vec3 hue){

    float baseColor = abs(mod((uv.y + iTime),1.0) * 2.0 - 1.0);
    vec3 linearColor;

    //initially linear
    linearColor = vec3(baseColor) * hue;

    if ( uv.x > 0.3333){

        //Interpret as though stored as gamma 2.2
        if (uv.x <= 0.6666){

            linearColor.x = pow(linearColor.x, 2.2);
            linearColor.y = pow(linearColor.y, 2.2);
            linearColor.z = pow(linearColor.z, 2.2);

        //Interpret as though stored as srgb
        } else{

           linearColor = SRGBToLinear(linearColor);

        }
    }



    //Assume monitor is already gamma 2.2, so correct for that
    linearColor.x = pow(linearColor.x, 1.0/2.2);
    linearColor.y = pow(linearColor.y,1.0/2.2);
    linearColor.z = pow(linearColor.z,1.0/2.2);


    return linearColor;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //hues
    vec3 hue1 = vec3(0.6,1.0,0.0);
    vec3 hue2 = vec3(1.0,0.5,0.2);
    vec3 hue3 = vec3(0.6,0.3,0.8);
    vec3 hue4 = vec3(1.0,1.0,1.0);



    //different hue for each panel
    vec3 color;
    if (uv.y >= 0.5){

        if(uv.x <= 0.5)
            //each panel is normalized to its own uv coordinates
            //it'd be better to add borders but I'm laaaaazy
            color = panelColor((mod(uv,0.5) * 2.0 ),hue1);
        else
            color = panelColor((mod(uv,0.5) * 2.0 ),hue2);
    }else {
        if(uv.x <=0.5)
            color = panelColor((mod(uv,0.5) * 2.0 ),hue3);
        else
            color = panelColor((mod(uv,0.5) * 2.0 ),hue4);

    }


    //output :)
    fragColor = vec4(color,1.0);
}
