const vec2 vp = vec2(320.0, 200.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float t = iTime * 10.0;

    float scale = .35;

	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p0 = (uv - 0.5) * vp;
    vec2 hvp = vp * 0.5;
	vec2 p1d = vec2(cos( t / 98.0),  sin( t / 178.0)) * hvp - p0;
	vec2 p2d = vec2(sin(-t / 124.0), cos(-t / 104.0)) * hvp - p0;
	vec2 p3d = vec2(cos(-t / 165.0), cos( t / 45.0))  * hvp - p0;
    float sum = 0.5 + 0.5 * (
		cos(length(p1d) / 30.0 * scale) +
		cos(length(p2d) / 20.0 * scale) +
		sin(length(p3d) / 25.0 * scale) * sin(p3d.x / 20.0 * scale) * sin(p3d.y / 15.0 * scale));

    float fractSum = sum;

    vec3 c = vec3(0.,0.,0.);

    if (fractSum > 0.8) {
        c = vec3(0.545, 0.164, 0.160);
    } else if (fractSum > 0.6) {
        c = vec3(0.749, 0.224, 0.196);
    } else if (fractSum > 0.4) {
        c = vec3(0.808, 0.373, 0.251);
    } else if (fractSum > 0.2) {
        c = vec3(0.867, 0.553, 0.345);
    } else {
        c = vec3(0.941, 0.808, 0.514);
    }

    float ms = 1.; //fract(fractSum * 5.0) * 0.1 + 0.9;


    fragColor = vec4(c * ms, 1.0);
}
