vec2 random2( vec2 p ) {
    return fract(sin(vec2(dot(fract(sin(vec2(dot(p*0.021,vec2(127.1,311.7)),dot(p*0.021,vec2(269.5,183.3))))*43758.5453),vec2(127.1,311.7)),
    dot(fract(sin(vec2(dot(p * 0.001,vec2(127.1,311.7)),dot(p * 0.001,vec2(269.5,183.3))))*43758.5453),vec2(269.5,183.3))))*43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 offset = vec2(iTime * 0.01, 0.0);
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pv = (fragCoord/iResolution.y + offset) * 1000.0;
    vec2 lv = (fragCoord/iResolution.y + offset)* 10.0;
    // Pixel Value and Light Value

    float illumination = random2(floor(pv)).x;
    float time = iTime * 0.1 + 6.0;
    vec3 col = vec3(0.0);
    float minL = 199.0;

    for(int x = -2; x < 4; x++){
        for(int y = -2; y < 4; y++){
            vec2 rand = random2(floor(lv + vec2(x,y)));
            vec2 lightPos = vec2(sin(rand.x * time), cos(rand.y * time)) + floor(lv) + vec2(x,y);
            vec3 lightCol = vec3(random2(floor(lv + vec2(x,y))+ vec2(.05)).x * 0.1,
                                 random2(floor(lv + vec2(x,y))+ vec2(.15)).y * 0.4,
                                 random2(floor(lv + vec2(x,y))+ vec2(.25)).x * 0.7);
            col += illumination * lightCol / length(lightPos - lv);
            minL = min(minL, length(lightPos - lv));
        }
    }
    col *= 0.03;


    // Output to screen
    fragColor = vec4(col, 1.0);
}
