vec3 testpalette(float t){
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.6,0.6,0.6);
    vec3 c = vec3(0.7,0.7,0.7);
    vec3 d = vec3(0.78,0.87,0.87);

    return a + b * cos(5.55656 * (c*t*d));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv; 
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col2 = vec3(0.2);
    vec3 col3 = testpalette(length(uv) * 0.9 + iTime);
    float d = length(uv) * exp(-length(uv0));
    float c = length(uv) * exp(+length(uv0));
    float factor = 8.;
    float a = length(uv) * exp(-length(uv0));

    a = sin(a*9. + iTime)/3.;

    c =  sin(c*8. + iTime)/9.;
    c = abs(c *= 1.);
    d = sin(d*factor + iTime)/factor;
    d = abs(d);

    d = 0.1 / d;

    col2 += col * d * col3 * c + a;

    // Output to screen
    fragColor = vec4(col2,0.12);
}
