float random1D(float value,float a,float b){
	//make value more random by making it bigger
	float random = fract(sin(value+b)*a);
    return random;
}

vec3 random2D(vec2 value)
{
    return vec3(
        random1D(value.x,14375.5964,0.546),
        random1D(value.y,18694.2233,0.153),
        random1D(value.x,19663.6565,0.327)
    );
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 col= random2D(0.5*cos(uv.xy+iTime)+0.5);
    // Output to screen
    fragColor = vec4(col,1.0);
}
