#define GRIDSIZE 6.0
#define LINEGRAY 0.5
#define LINESIZE 0.1
#define CR vec3(1.0, 0.0, 0.0)
#define CG vec3(0.0, 1.0, 0.0)
#define CB vec3(0.0, 0.0, 1.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 griduv = uv * iResolution.xy / GRIDSIZE;
    vec2 LEDuv = griduv;
    LEDuv.x *= 3.0;
    LEDuv = fract(LEDuv);
    vec2 line = vec2(ceil(LEDuv.x - LINESIZE * 3.0), ceil(LEDuv.y - LINESIZE));
    line = clamp(line + LINEGRAY, 0.0, 1.0);

    float rgbMix = fract(griduv.x);
    vec2 pixeluv = floor(griduv) * GRIDSIZE / iResolution.xy;
    vec3 oc = texture(iChannel0, pixeluv).rgb;

    vec3 rgColor = mix(CR * oc.r, CG * oc.g, ceil(rgbMix - 1.0 / 3.0));
    vec3 rgbColor = mix(rgColor, CB * oc.b, ceil(rgbMix -2.0 / 3.0));

    vec3 col = rgbColor * min(line.x, line.y);
    // Output to screen
    fragColor = vec4(col,1.0);
}
