#define radius 0.25

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord-.5*iResolution.xy ) / iResolution.y;

    float square1 = max(uv.x + uv.y, uv.x - uv.y);
    float square2 = min(uv.x + uv.y, uv.x - uv.y);
    float circle1 = length(uv + vec2(radius / 2.0, -radius / 2.0)) * sqrt(2.0);
    float circle2 = length(uv + vec2(-radius / 2.0)) * sqrt(2.0);

    float square = max(square1, -square2);
    float circles = min(circle1, circle2);

    float heart = min(square, circles);

    fragColor = vec4(smoothstep(1.2, 0.8, heart * 4.0));

}
