void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float d = distance(fragCoord, vec2(0,0));

    float b = 0.;

    if (d < 500.) {
        b = 1.;
    } else if (d < 900.) {
        b = 0.75;
    } else if (d < 1300.) {
        b = 0.5;
    } else if (d < 1700.) {
        b = 0.25;
    } else if (d < 2100.) {
        b = 0.15;
    } else if (d < 2500.) {
        b = 0.1;
    }

    fragColor = vec4(0.2, 0.2, 1. * b,1.0);
}
