// Noise Worlds
// Leon Denise
// 2023/09/08

// gyroid pattern
float gyroid (vec3 p)
{
    return dot(sin(p),cos(p.yzx));
}

// noise
float fbm (vec2 p)
{
    vec3 q = vec3(p, 0);
    float result = 0., a = .5;
    for (float i = 0.; i < 4.; ++i, a /= 2.)
    {
        q += (result-.5);
        result += gyroid(q/a)*a;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coordinates
    vec2 p = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    p += iTime * 0.1;

    // shape
    float e = .3;
    float diff = abs(fbm(p+vec2(0,e))-fbm(p-vec2(0,e)))/e;
    float circle = length(fract(p*.5)-.5);
    float thin = smoothstep(.5,.0,circle) * 2.;
    float shape = smoothstep(.01/thin,0.,diff-thin+.1);
    fragColor = vec4(shape);
}
