#ifdef GL_ES
precision mediump float;
#endif



vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*sin( 6.28318*(c*t+d) );
}

float plot(vec2 uv,vec2 res){

    float gridSize = 15. + abs(sin(iTime)*13.) ;

    int mx = int(mod(uv.x * res.x + (200. + sin(iTime)*60.),gridSize));

    int my = int(mod(uv.y * res.y + (200. + cos(iTime)*60.),gridSize));

    if(mx != 0 && my != 0) {
        return 1.;
    }
    return .0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy);
    float p = plot(uv,iResolution.xy);
    vec4 color;
    vec3 p1 = pal( uv.y, vec3(0.5,0.5,0.5),vec3(0.5,0.3,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67));
    vec3 p2 = pal( uv.x, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.10,0.20) );
    if(p == 1.0)
        color = vec4(p1,1.0);
    else color = vec4(p2,1.0);

    fragColor = color;
}
