#define PI 3.141592653589793
#define sqrt3 1.73205

// Customise
#define scaling 2.0
#define rippleLength .55
#define rippleSlowDownFactor 2.0


// Based on:
// The Art of Code - Hexagon Tiling Explained!
// https://www.youtube.com/watch?v=VmrIDyYiJBA
float hexDist(vec2 p) {
    p = abs(p);
    float c = dot(p, normalize(vec2(1, sqrt3)));
    c = max(c, p.x);

    return c;
}

// Based on:
// The Art of Code - Hexagon Tiling Explained!
// https://www.youtube.com/watch?v=VmrIDyYiJBA
vec4 hexCoords(vec2 uv) {
    vec2 r = vec2(1, sqrt3);
    vec2 h = r / 2.;
    vec2 ga = mod(uv, r) - h;
    vec2 gb = mod(uv - h, r) - h;
    vec2 gv;
    if (length(ga) < length(gb)) gv = ga; else gv = gb;

    float angle = atan(gv.x, gv.y);
    float edgeDist = 0.5 - hexDist(gv);
    vec2 id = uv - gv;

    return vec4(angle, edgeDist, id.x, id.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Coords
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    uv *= scaling;
    vec4 hc = hexCoords(uv);

    // Angular ripple effect - adapted from:
    // python273 - Triangular pattern
    // https://www.shadertoy.com/view/DsjXDK
    float a = (1. + atan(hc.w, hc.z) / PI) / 2.;
    float l = length(uv) / length(vec2(rippleLength));
    a = fract(a + l / 1. - iTime / rippleSlowDownFactor);

    // Color
    vec3 col = vec3(0);
    col.rb = a*hc.zw - a*hc.wz;
    col.r = abs(a*(hc.z ));
    col.b = abs(a*(hc.w - hc.z));
    fragColor = vec4(col, 1.0);
}
