//  Everyone's favorite action hero rendered using a dice mosaic style.
//
// ---------------------------------------------------------------------------------------

#define RES 50.
#define pip(X, Y) smoothstep(.1 + RES / iResolution.y, .1, length(p - vec2(X, Y)))

float die(vec2 p, int pips)
{
    if (pips == 1) { return pip(0, 0); }
    if (pips == 2) { return max(pip(-.25, -.25), pip(.25, .25)); }
    if (pips == 3) { return max(max(pip(-.3, -.3), pip(0, 0)), pip(.3, .3)); }
    if (pips == 4) { return max(max(max(pip(-.25, -.25),pip(.25, -.25)), pip(.25, .25)), pip(-.25, .25)); }
    if (pips == 5) { return max(max(max(max(pip(-.3, -.3),pip(.3, -.3)),pip(0, 0)), pip(.3, .3)), pip(-.3, .3)); }
    if (pips == 6) { return max(max(max(max(max(pip(-.25, -.3),pip(.25, -.3)),pip(.25, 0)),pip(-.25, 0)), pip(.25, .3)), pip(-.25, .3)); }
    return 0.;
}

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         u = U / R.y;

    vec2 m = fract(u * RES) - .5;
    float o = texture(iChannel0, vec2(R.y / R.x, 1) * trunc(u * RES) / RES).r;
    O = vec4(die(m, int(o * 6.)));
}
