vec2 points[5];

// smooth min
float smin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

float getDist(vec2 p) {
    for (int i = 0; i < points.length(); i++) {
        points[i] = p;
        points[i].x += (cos(iTime*float(i)))/4.;
        points[i].y += (sin(iTime*float(i)))/4.;
    }
    float amt = .2;
    float s = .075;
    float dist = smin(smin(smin(sdCircle(points[4],s),sdCircle(points[3],s),amt),sdCircle(points[2],s),amt),sdCircle(points[1],s),amt);
    return dist;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float d = getDist(uv);

    vec3 col = vec3(1.-smoothstep(.0,.005,d));

    fragColor = vec4(col,1.0);
}
