#define PI 3.14159265359
const float sp = .5;
const float num_samples = 128.0;
const float hh = 1.0;
const float kstep = 0.1;
const float ksum = 5.0;
const float eatt = 2.0;

vec3 attenuation(vec3 c, float d)
{
    return c/exp(eatt*d);
}


vec3 radial_blur_filter(in vec2 origin, in vec2 point)
{
    vec4 fon = texture(iChannel0, point);
    vec3 col = fon.rgb * fon.a;
    vec3 light = vec3(0.0);
    vec2 v = point - origin;
    float l = length(v);
    v = normalize(v); 
    float dz =  hh / num_samples * ksum;
    float l1 = l/(1.0 + hh);
    float al = (l-l1);
    for(float s = 0.0; s < num_samples; s++)
    {
        float l2 = l1 + al*s/num_samples;
        vec2 p = origin + v*l2;

        vec3 c = texture(iChannel0, p).rgb;
        float pst = smoothstep(kstep, 1.0, c.g);
        c *= pst;

        float fi = atan(l2, 1.0);
        float d = (l - l2)/sin(fi);
        c = attenuation(c, d);
        light += c;
    }
    light *= dz;
    float pst = smoothstep(0.0, 1.0, col.g);  
	light*=(1.0 - pst);

    col += light;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pt = fragCoord.xy/iResolution.xy;
    vec4 col = texture(iChannel0, pt);
    vec2 mouse = vec2(0.5 + cos(iTime*sp),0.5+sin(iTime*sp));
    col.rgb = radial_blur_filter(mouse, pt);
    fragColor = col;

}
