vec3 xen(vec2 uv) {
    vec3 color = vec3(1.);
    vec2 st = uv;

    //if (uv.y > .25) return vec3(1.0,0.0,0.0);
    //vec2 fft_range = fft_start+uv.yy/(fft_end-fft_start);
    vec2 fft_range = uv.yy/2.;

    vec4 tex = vec4(texture(iChannel0, fft_range).xyz,1.0);
    float w = tex.x;
	float wd = (1.-w)/2.;
	float f = step(0.+wd, st.x) - step(1.-wd, st.x);
	f = 1.-f;

    color = vec3(f);

    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 fft = xen(uv);
    vec3 col = fft;

    fragColor = vec4(col,1.0);
}
