// Iteration of https://www.shadertoy.com/view/clj3Dt.

const float thickness = .0005;
const float major_interval = 0.25;  // frac of width for major ticks

float S(vec2 uv, float interval, float weight) {
    float pixel_width = 1.5/iResolution.x;

    vec2 offset = vec2(thickness * weight);

    vec2 m = mod(uv, interval);

    vec2 r = smoothstep(pixel_width, 0., m - offset);
    vec2 p = smoothstep(interval-pixel_width, interval, m + offset);
    return max(r.x+p.x, r.y+p.y);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec4 background_color = vec4(.8984375, .8984375, .91796875, 1.);
    vec4 line_color = vec4(.596078, .6784314, .9137255, 1.);

    vec2 uv = fragCoord/iResolution.x;

    float d;
    d += S(uv, major_interval/1.0, 1.0);
    d += S(uv, major_interval/5.0, 0.5);
    d += S(uv, major_interval/50., 0.25);

    fragColor = mix(background_color, line_color, min(1.0, d));

}
