float random(float x){
    return fract(sin(x)*336645.3452234);
}

vec3 colorRandom(vec2 st){
    vec3 A = vec3(0.2, random(st.y), 0.5);
    vec3 B = vec3(0.804,0.4, random(st.y));
    return mix(A, B, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord/iResolution.xy;

    st = st * 4. - 2.;
    st.x *= iResolution.x/iResolution.y;

    float ran = random(st.x);

    st *= sin(iTime * ran);
    float circle = step(length(st), 0.368);

    vec3 color = vec3(0.);
    color = vec3(circle) * colorRandom(st);;
    fragColor = vec4(color,1.0);
}
