float spiralSDF(vec2 st, float t){

    //st -= 0.3;
    float r = dot(st,st);
    float a = atan(st.y, st.x);
    return fract(fract(fract(log(r)*t + iTime+a*0.159)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2.0 * fragCoord/iResolution.xy - 1.0;
    uv.x *= iResolution.x/iResolution.y;

    float p_angle = atan(sin(uv.x)*1.5, sin(uv.y)*2.4);
    float p_distance = length(uv) * 1.6;

    vec2 st = vec2(p_distance, p_angle);

    vec3 col = vec3(0.5, 0.3, 0.1) / texture (iChannel0, vec2(2.0 / st.x  + iTime, st.y - iTime),  fract(spiralSDF(uv, 1.0))).xyz;

    col *= mix(st.x, spiralSDF(uv, 1.2), (fract(p_angle) * 7.0) );

    fragColor = vec4(col,1.0);
}
