void mainImage(out vec4 O, vec2 F){
	float p, s, y, c, h=3.,
					  e=iTime*.4+.8;

	vec2 r = iResolution.xy,
		 v = (F*2.-r)/r.y;

	for(s = 0.; s < 2e2 && abs(h) > .001 && p < 40.; s++){
		vec3 o	= p*normalize(vec3(1., v));
		c		= sin(e + p*.5)*.25;
		y 		= c + .25;
		o.x	+= e;
		o.y	= abs(o.y);
		o		= fract(o) - .5;
		o.xy	*= mat2(cos(e + vec4(0,33,11,0)));
		o.y	+= y/2.;
		o.y	-= clamp(o.y, 0., y);
		p += h	= (length(o) - .1*(.75 + p*.1 + c))*.8;
	}
	O.rgb = exp(-p*.15 - .5*length(v)) * (cos(p*(8.4 + 0.16*vec3(0,1,2)))*1.2+1.2);
}
