void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
     if(uv.y>0.33)
    {
       // Output to screen
       fragColor = vec4(1,0,0,1);
    }

    else
    {
       fragColor = vec4(0.9,0.9,0,1);
    }
    // Output to screen
     if(uv.y>0.66)
     {
       fragColor = vec4(0,0,0,1);
    }
}
