# define PI 3.141592653589793;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 uvraw = uv;
    uv*=0.6;
    uv+=vec2(0.5,-0.2);
    vec3 col = mix(vec3(1.0,0.3,0.0),vec3(0.1,0.08,0.05),uvraw.y);
    float r = 0.2-0.1*cos(atan(uv.y,uv.x)*10.0+20.0*uv.x+3.0);
    col*= smoothstep(r,r+0.01,abs(length(uv)));
    r=0.02+0.002*cos(uv.y*50.0);
    r+=exp(-20.0*(uvraw.y+1.0));
    float drunk = smoothstep(r,r+0.01,abs(uv.x-0.5*sin(uv.y*2.0)));
    col*=1.0 -(1.0-drunk)*(1.0-smoothstep(0.0,0.1,uv.y));
    fragColor = vec4(col,0.0);
}












