void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy;
    float t = iMouse.z > 0. ? iMouse.x/R.x                             // manual tuning
                      : fract(iTime/30.);                          // demo mode

    int m = int(255.*texelFetch( iChannel0, ivec2(u)%1024, 0 )), // bluenoise mask
        s = int(256.*t);                                         // selected value

    O = vec4(                    m == s                          // left: select one
              || u.x/R.x > .5 && m == s+1                        // right: select two in a row
            );
    if ( int(u) == int(R)/2 ) O = vec4(1,0,0,1);                 // red separator
    if ( u.y < 5. ) O = vec4(1, u.x/R.x > t, 0,1);               // progress bar = value

 // O.g += .5* sqrt(smoothstep( 1., 0., abs(length( u-100.)-64.) ) 
 //               + smoothstep( 1., 0., length( u-100.+64.*cos(6.28*t*16.+vec2(0,11)))-3. )); 
}
