vec3 palette( float t ) {
    vec3 a = vec3(0.778, 0.498, 0.058);
    vec3 b = vec3(2.638, 1.308, 0.748);
    vec3 c = vec3(1.218, 1.168, 0.838);
    vec3 d = vec3(0.000, 0.248, 0.698);

    return a + b*cos(6.28318*(c*t*d) );
}



#define S(a, b, t) smoothstep(a, b, t)

float Boxc(vec2 p, float wb, float wt, float yb, float yt, float blur) {
    float c = S(-blur, blur, p.y-yb);
    c *= S(blur, -blur, p.y-yt);

    p.x = abs(p.x);

    float w = mix(wb, wt, (p.y-yb/2.) / (yt - yb/2.));
    c *= S(blur, -blur, p.x-w*.4);

    return c;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) 
{
   vec2 uv = (fragCoord-.5* iResolution.xy) / iResolution.y;
   vec2 uv0 = uv;


   vec3 col = vec3(0.0);
   vec3 colc = palette(length(uv) + iTime/1.1);
   vec3 finalcol = vec3(0.0);

   for (float i = 0.0; i < 3.; i++) {
       uv = fract(uv *3.) -0.5;
       float d = length(uv) * exp(-length(uv0));

       uv.xy *= iTime/30.;


       float c = Boxc(uv, 3., 0., -3., 3., .0005);

       c *= iTime*.5;

       d = sin(d*12. + iTime/3.)/5.;

       d = 0.009/d;



       colc += c;
       finalcol =  colc * d;


       float thickness = 1./iResolution.y;
       if(abs(uv.x)<thickness) col.g =1.;
       if(abs(uv.y)<thickness) col.b =1.;


   }
   fragColor = vec4(finalcol, 1.0);
}
