float s(in float steppi,in float v){
    return smoothstep(steppi,steppi+(3./iResolution.y),v);
}

float circleOutline(in vec2 pos, in float r,in float t, in vec2 uv){
    float d = distance(pos,uv);
    return s(r-t,1.-d) * s(r,d);
}
float circle(in vec2 pos, in float r, in vec2 uv){
    float d = distance(pos,uv);
    return s(r,d);
}



float sineWave(in vec2 uv,in float height, in float time, in float speed,in float offset) {
    float y = uv.y-height;
    float x = uv.x*14.-(time*speed+offset);
    return smoothstep((1.5/iResolution.y)*13., 0.0, abs(y*10. - sin(x)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    vec2 mid = vec2(iResolution.x/iResolution.y/2.,.5);
    float circleSize = .4;
    float circleThick = -0.18;
    float timeOfSin = 2.*sin(iTime)+1.;

    float amp = 1.5;
    float offset = amp*sin(iTime)+(amp/2.);

    vec2 pos = mid-uv;
    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x);
    float f = abs(cos(a*2.5+iTime)*1.8*cos(a+.4))*0.5+0.3*sin(timeOfSin+2.)+.2*cos(2.1*iTime);


    vec3 col = vec3(0.);

    float circleBitMap = circleOutline(mid,circleSize,circleThick,uv);

    col += vec3(circleBitMap);
    col += vec3(0.110,0.443,0.847)*sineWave(uv,.4,iTime,4.0,offset)*(1.-circle(mid,circleSize,uv));
    col += vec3(0.506,0.239,0.612)*sineWave(uv,.44,iTime,4.,2.*offset)*(1.-circle(mid,circleSize,uv));
    col += vec3(0.200,0.820,0.478)*sineWave(uv,.48,iTime,4.,3.*offset)*(1.-circle(mid,circleSize,uv));
    col+= normalize(vec3(.213*pow(timeOfSin,1.9),.449*pow(timeOfSin,.9),.29*pow(timeOfSin,2.1)))*f*(circle(mid,circleSize,uv));

    // Output to screen
    fragColor = vec4(col,1.0);
}
