void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord - iResolution.xy*0.5)/(max(iResolution.x, iResolution.y)*0.5);

    float t = iTime * 2.;
    float pulse_t = sin(t*0.003)*100. + sin(t*.5);
    vec2 dir = vec2(sin(pulse_t), cos(pulse_t));
    float k = dot(dir, uv.xy);

    vec3 rainbow = vec3(0.5+sin(uv.x+t)*0.5, 0.5+cos(uv.y+t)*0.5, 1);
    vec3 col = vec3(0);
    int N = 20;
    for(int i=0; i<N; i++){
        float I = float(i)/10.;
        float d = sqrt(pow(uv.x, 2.) + pow(uv.y, 2.)) + sin(k*15. + t*3. + I*3.)*(0.02+I*0.01) - .2 - I*0.1 + sin(t)*0.1;
        float l = exp(-pow((d)*200., 2.));
        col += l * I;
    }

    col *= rainbow;


    // Output to screen
    fragColor = vec4(col,1.0);
}
