// Green screen function from @iq 

// :D :D :D
vec4 compositeGreenScreenTexture(vec2 uv, vec4 background)
{

     vec4 tex = texture(iChannel0, uv);

     float maxrb = max( tex.r, tex.b );
     float k = clamp( (tex.g-maxrb)*5.0, 0.0, 1.0 );

     float ll = length( tex );
     tex.g = min( tex.g, maxrb*0.8 );
     tex = ll*normalize(tex);

     return mix(tex, background, k);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //get three lines with varying offsets
    vec2 uv_r = uv;
    vec2 uv_b = uv + (iTime*0.05);
    vec2 uv_g = uv - (iTime*0.05);


    // Time varying pixel color
    float r = sin((uv_r.x + uv_r.y) * 15.0);
    float g = sin((uv_g.x + uv_g.y) * 15.0);
    float b = sin((uv_b.x + uv_b.y) * 15.0);
    vec4 col = vec4(r, g, b, 1.0);

    // Output to screen
    fragColor = col;
}
