// SANIVELEVINAS
// Based on @patriciogv, Title: CellularNoise

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}


vec2 random( vec2 p ) 
{
    return fract(sin(vec2(dot(p,vec2(127.1,311.7)),
    dot(p,vec2(269.5,183.3))))*43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec3 color = vec3(.0);

    // Scale
    uv *= 6.;

    // Tile the space
    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);

    float m_dist = 10.;  // minimum distance

    for (int y= -1; y <= 1; y++) 
    {
        for (int x= -1; x <= 1; x++) 
        {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            // Random position from current + neighbor place in the grid
            vec2 point = random(i_uv + neighbor);

			// Animate the point
            point = 0.5 + 0.5*sin(iTime + 6.2831*point);

			// Vector between the pixel and the point
            vec2 diff = neighbor + point - f_uv;

            // Distance to the point
            float dist = length(diff);

            // Keep the closer distance
            m_dist = min(m_dist, dist);
           }
    }
    // Draw the min distance (distance field)
    color += m_dist;

    // Draw cell center
    color += 1.-step(.02, m_dist);

    //
    color = palette(fract(color.x*sin(iTime)), vec3(0.7), vec3(0.3), vec3(0.9), vec3(0.9, 0.11, 0.66));


    fragColor = vec4(color,1.0);
}
