#define PI 3.14159265359

float fu(float x)
{
    float f = 0.3 + 0.15*cos(x*10.0) + 0.05*sin(x*20.0) + 0.015 * cos(x*30.0) + 0.015*sin(x*35.0); 
    return f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec3 color = vec3(1);
    float f = fu(uv.x + iTime*0.1); 
    float f2 = fu(uv.x + PI/10.0 - iTime*0.1);  //reflection
    f = (f + f2);

    float d = f/7.0;
    float c = fract((f-uv.y)/d);

    if (f > uv.y)
        color = vec3(0.0, 0.0, c);
    fragColor = vec4(color,1.0);
}
