void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0,fragCoord/iResolution.xy)*.5+.5;
}

void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 C = texture(iChannel0, (u+vec2(0,0))/iResolution.xy);
    vec4 R = texture(iChannel0, (u+vec2(1,0))/iResolution.xy);
    vec4 L = texture(iChannel0, (u-vec2(1,0))/iResolution.xy);
    vec4 U = texture(iChannel0, (u+vec2(0,1))/iResolution.xy);
    vec4 D = texture(iChannel0, (u-vec2(0,1))/iResolution.xy);
    vec2 m = 1.*(2.*iMouse.xy-iResolution.xy)/iResolution.y;
         m = clamp(m,vec2(-.5),vec2(.5));
    float pi = 3.14159265359;
    if((iFrame&1) == 0){C.x += +(+L.y -C.y)*m.x
                               +(+D.y -C.y)*m.y;}
    if((iFrame&1) != 0){C.y += +(-R.x +C.x)*m.x
                               +(-U.x +C.x)*m.y;}
    vec2 uf = u-iResolution.xy*.5;
    if(iFrame == 0) C = 1./exp(dot(uf,uf)*.01)*vec4(1,1,0,0);
    float keyW = texture(iChannel2, vec2(87.5/256.,.25)).x;
    if(keyW!=0.) C = (texture(iChannel1, u/iResolution.xy).x*2.-1.)*vec4(1,1,0,0);
    fragColor = C;
}
