void mainImage( out vec4 o, in vec2 FC )
{
float i,d=.1,e;vec3 p;
o=vec4(0.);

for(float i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*2.;
    p=vec3(log(length(p)),cos(p.y+iTime),atan(-p.y,p.z)*iTime);  
    p-=abs(abs(p)-.03);
    d=pow(length(p.xy),12.);
    d-=dot(p,p)/6e4;

e+=d;
}
o+=vec4(p,1)/i;
}
