#define R iResolution
#define T ((iTime+5.5988)*0.2)

uint hash(in float seed) {
    uint s = floatBitsToUint(seed);
    uint x = 532U*~s+315U;
    uint y = 774U*s+643U;
    s += ((x*323U+y) + (~(x ^ y) >> 6U))*23U;
    s ^= s << 17U;s ^= s >> 13U;
    x = x + ((x^(x << s)) << 3U); s ^= s << 5U;
    y = y + ((y^(y << s)) >> 2U); s = s * x + y * 90389U; s ^= (s << 5U);
    return s;
}

float hash21(in vec2 ip, in float seed) {
    uint s = hash(seed);
    uvec2 p = uvec2(hash(ip.x), hash(ip.y));
    p = (p * s + 94821U) + (~p << 3U*s);
    p ^= (p << 11U); p ^= (p >> 9U); p ^= (p << 8U); p = p * 40892U;
    return float(p.x * p.y + s) / float(0xFFFFFFFFU);
}

float noise21(in vec2 p, in float seed) {
    vec2 id = floor(p);
    vec2 lv = fract(p);
    lv = lv*lv*(3.0-2.0*lv);
    return mix(
        mix(hash21(id, seed), hash21(id+vec2(1, 0), seed), lv.x),
        mix(hash21(id+vec2(0, 1), seed), hash21(id+vec2(1, 1), seed), lv.x),
        lv.y
    );
}

float noise21(in vec2 p, in float seed, in float freq, const in int octaves) {
    p += 931.3982818;
    float n = 0.0;
    float div = 0.0;
    float amp = 1.0;
    for (int i = 0; i < octaves; i++) {
        n += amp*noise21(p*freq, seed); div += amp; amp /= 1.5; freq *= 1.5;
    }

    return n/div;
}

void mainImage( out vec4 O, in vec2 fc )
{
    vec3 col = vec3(0.0);
    vec2 uv = fc.xy/R.y;
    vec4 m = vec4(iMouse.xy/R.y, iMouse.zw);
    uv += vec2(cos(T), sin(T));
    m.xy += vec2(cos(T), sin(T));
    float ra = 0.1;
    float fe = ra*0.999;
    float s = (1.0-smoothstep(ra-fe, ra+fe, distance(uv, m.xy)))*float(m.z > 0.001);
    float mask = noise21((uv+s)*0.5, 33.398291, 16.0, 6);
    float mt = (T+9.4822)*0.8747;
    vec2 mk = 0.05*vec2(sin(mt), cos(mt))*(0.5+(mask*0.5));
    mask = 1.0-smoothstep(0.2+(mk.x*0.5), 0.7+(mk.y*0.9), mask);
    uv = mix(uv, uv*(0.003123+sin(T+uv.y-uv.x)), mask*0.05);
    uv += cos(T+sin(uv.x*3.3)-cos(uv.y*3.39828))*mask;
    col += noise21((uv+s)*0.5,5123.23, 16.0, 6);
    col = mix(col, col*vec3(1, 0, 0), mask*mask);
    O = vec4(col, 1.0);
}
