vec3 palette(in float t)
{
    vec3 a = vec3(0.5,0.5,0.5);
    vec3 b = vec3(0.5,0.5,0.5);
    vec3 c = vec3(1.,1.,1.);
    vec3 d = vec3(.32,.18,1.);
    return a + b*cos( 6.28318*(c*t+d) );
}

vec2 rotate(vec2 p,float a){
	float c = cos(a);
    float s = sin(a);
    return p*mat2(c,s,-s,c);
}
float map(vec3 p){
    for( int i = 0; i<8; ++i){
        float t = iTime*0.2;
        p.xz =rotate(p.xz,t);
        p.xy =rotate(p.xy,t*1.89);
        p.xz = abs(p.xz);
        p.xz-=.5;
	}
	return dot(sign(p),p)/5.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) /iResolution.y;
    uv = rotate(uv,(iTime+sin(iTime)*0.5)*0.1);

    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.);

    float target = 2.;
    for (float i = 1.;i < target;i+=i/4.){

        uv = fract(uv * 1.5) - .5;

        float d = length(uv) * exp(1.-length(uv0));
        //d = abs((2.*sin(floor(d+iTime)))*sin(d));


        vec3 col = palette(length(uv0) + i*.4 + iTime*0.4);

        float t = 8.;
        d = sin(d*t+iTime)/t;
        d = abs(d);

        //d = pow(0.01 / d,1.2);
        d = pow(0.015 / d,2.);

        finalColor += col * d;

    }
    finalColor -= map(finalColor)*.5;

    fragColor = vec4(finalColor,1.0);
}
