void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Background color rgb(49/255, 169/255, 238/255, 255/255) -- 0.192156862745098, 0.6627450980392157, 0.9333333333333333
    vec4 texture_color = vec4(0.192156862745098, 0.6627450980392157, 0.9333333333333333, 1.0);

    vec4 k = vec4(iTime) * 0.8;
    k.xy = uv * 7.0;


    mat3 transformationMatrix = mat3(vec3(-2.0, -1.0, 0.0), vec3(3.0, -1.0, 1.0), vec3(1.0, -1.0, -1.0));

    float val1 = length(0.5 - fract(k.xyw *= transformationMatrix * 0.5));
    float val2 = length(0.5 - fract(k.xyw *= transformationMatrix * 0.2));
    float val3 = length(0.5 - fract(k.xyw *= transformationMatrix * 0.5));

    float minValue = min(min(val1, val2), val3);
    vec4 color = pow(vec4(minValue), vec4(7.0)) * 3.0 + texture_color;

    fragColor = color;
}
