#define wmod(p, w) mod(p, w) - w/2.
#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define norm(p) normalize(vec3(map(p + e), map(p + e.yxy), map(p + e.yyx)) - map(p))

float map(vec3 p) {
    vec2 c;

    p = abs(fract(p / 3.) * 3. - 1.5);
    c.x = min(
              max(p.x, p.y), 
              min(
                  max(p.y, p.z), 
                  max(p.x, p.z)
              )
          ) - .5;

    p = abs(fract(p * 4. / 3.) * 3. / 4. - .1);
    c.y = min(
              p.x, 
              min(p.y, p.z)
          );

    return 
        max(
            abs(c.x), 
            abs(c.y)
        ) * .75 
        + length(c) * .3 
        - .13;
}


void mainImage(out vec4 o, vec2 u) {
    float d, s, i, b,
          t = iTime;

    vec2 R = iResolution.xy;
         u = (u.xy - R * .5) / R.y;

    vec3 rd = normalize(vec3(u, 1. - dot(u, u) * .4)),
         p, k,
         e = vec3(.01, 0, 0);

    rd.xz *= rot(t * .3);
    rd.xy *= rot(t * .2);
    o *= 0.;

    k = vec3(cos(t) * 4., sin(t) * 1.5, t + 1.);

    while(i++ < 44.) {
        b = length(wmod(p - k, vec3(4, 3, 11))) - .5; 
        d += max(s = min(s = abs(map(p)), b), 2e-3);

        p = d * rd;
        p.z += t; 

        s < .02 
            ? o.rgb += (.015 - s) / d * i / 8. + reflect(norm(p), k-p) * 1e-4 
            : o.rgb;
    }
}
